    /**
     * Display a listing of the resource.
     */
    public function index(User $user)
    {
        
        $tweets = Tweet::withCount('liked')
        ->orderBy('liked_count','desc')
        ->orderBy('id','desc')
        ->limit(10)
        ->get();
        return view('recommenduser.index',compact('tweets'));
    }

